import React, { Component } from 'react'
import Button from 'react-bootstrap/Button'

class BuyComponent extends Component{
  constructor(props){
    super();
    this.state = {refAddress: '-1'};
    this.handleChange = this.handleChange.bind(this);
    this.BuyItem = this.BuyItem.bind(this);
  }
  render(){
    return(
      <div>
        <span>Referrer ETH wallet address:</span><input type="text" name="refAddress" onChange={this.handleChange}/>
        <span>Buy 1 item:</span><Button onClick={this.BuyItem}>0.05 ETH</Button>
      </div>
    );
  }
  handleChange(e){
    this.setState({refAddress: e.target.value})
  }
  BuyItem(){
    console.log(window.web3.utils.toWei('60','Gwei'));
    var obj = {
        from: this.props.userDetail.account,
        gas: 8000000,
        gasPrice: window.web3.utils.toWei('60','Gwei'),
        gasLimit: 8000000,
        value: window.web3.utils.toWei('0.05'),
    };
    //0x73e612F58362f44Bb0Af24fA074B147b30389252
    this.props.userDetail.contract.methods.registrationExt(this.state.refAddress).call(obj).then((result)=>{
      console.log(result);
    },(err)=>{
      alert(err.message);
    });

  }
}

export default BuyComponent
