import 'bootstrap/dist/css/bootstrap.css';
import React, { Component } from 'react'
import Web3 from 'web3'
import './App.css'
import { _ABI, _CONTRACT_ADDRESS } from './config'
import Button from 'react-bootstrap/Button'

class App extends Component {

  componentWillMount() {
    //this.loadBlockchainData()
  }

  async loadBlockchainData() {
    if (!ethEnabled()) {
      alert("Please install an Ethereum-compatible browser or extension like MetaMask to use this dApp!");
    }
    else{
      const accounts = await window.web3.eth.getAccounts().then((accounts)=>{
        if (accounts.length === 0) {
          console.log('MetaMask is locked')
          this.setState({isLogin:false})
        }
        else {
          this.setState({isLogin:true})
          this.setState({ account: accounts[0]})
          window.web3.eth.getBalance(accounts[0]).then((value)=>{
            this.setState({ balance: window.web3.utils.fromWei(value)})
          });
        }
      },()=>{
        console.log("fail callback");
      })

      const contract = new window.web3.eth.Contract(_ABI, _CONTRACT_ADDRESS)
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      isLogin: false,
      account: '-1',
      balance: '-1'
    }
    this.LoginToMetamask = this.LoginToMetamask.bind(this);
    window.ethereum.on('accountsChanged', function (accounts) {
      // Time to reload your interface with accounts[0]!
    });
  }

  render() {
    if(this.state.isLogin){
      return (
        <div className="container">
          <h1>It's alive!</h1>
          <p>Your account: {this.state.account}</p>
          <p>Your balance: {this.state.balance} ETH</p>
        </div>
      );
    }
    else {
      return (
        <div className="container">
        <Button variant="primary" onClick={this.LoginToMetamask}>Connect to Metamask</Button>{' '}
        </div>
      );
    }
  }

  LoginToMetamask(){
    this.loadBlockchainData()
  }
}
const ethEnabled = () => {
  if (window.ethereum) {
    window.web3 = new Web3(window.ethereum);
    window.ethereum.enable();
    return true;
  }
  return false;
}

export default App;
