import React, { Component } from 'react'
import Button from 'react-bootstrap/Button'

class BuyComponent extends Component{
  constructor(props){
    super();
    this.state = {refAddress: '-1'};
    this.handleChange = this.handleChange.bind(this);
    this.BuyItem = this.BuyItem.bind(this);
  }
  render(){
    return(
      <div>
        <span>Referrer ETH wallet address:</span><input type="text" name="refAddress" onChange={this.handleChange}/>
        <span>Buy 1 item:</span><Button onClick={this.BuyItem}>0.05 ETH</Button>
      </div>
    );
  }
  handleChange(e){
    this.setState({refAddress: e.target.value})
  }
  BuyItem(){
    var obj = {
        from: window.web3.eth.accounts[0],
        gas: 3000000,
        gasPrice: 3000000,
        gasLimit: 3000000,
        value: 0.05,
    };
    this.props.contract.methods.registrationExt(this.state.refAddress).call(obj).then({

    });
    console.log(this.props.userDetail.contract);
    console.log(this.state.refAddress);
  }
}

export default BuyComponent
