import React, { Component } from 'react'
import Button from 'react-bootstrap/Button'
import Web3 from 'web3'

class BuyComponent extends Component{
  constructor(props){
    super();
    this.state = {refAddress: '-1'};
    this.handleChange = this.handleChange.bind(this);
    this.BuyItem = this.BuyItem.bind(this);
  }
  render(){
    return(
      <div>
        <span>Referrer ETH wallet address:</span><input type="text" name="refAddress" onChange={this.handleChange}/>
        <span>Buy 1 item:</span><Button onClick={this.BuyItem}>0.05 ETH</Button>
      </div>
    );
  }
  handleChange(e){
    this.setState({refAddress: e.target.value})
  }
  BuyItem(){
    var obj = {
        from: window.web3.eth.accounts[0],
        gas: 8000000,
        gasPrice: 8000000,
        gasLimit: 8000000,
        value: 0.05,
    };
    this.props.contract.registrationExt(
      this.state.refAddress,
      obj,
      function(error, txHash) {
          if (error) {
              console.log(error);
              that.showLoader(false);
          }
          // If success, wait for confirmation of transaction,
          // then clear form value
          else {
              that.waitForReceipt(txHash, function(receipt) {
                  that.showLoader(false);
                  if (receipt.status) {
                      console.log({ receipt });
                      this.setState({refAddress: ""})
                  } else {
                      console.log("error");
                  }
              });
          }
      }
    );
    //console.log(this.props.userDetail.contract);
    //console.log(this.state.refAddress);
  }
}

export default BuyComponent
